css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://(www.)?(?!support.)ixquick\.com/(?!.*aboutixquick/?.*|do/preferences.pl.*|.*privacy-policy\.html.*|.*protect-privacy\.html.*|.*press/.*|.*spread-the-word\.html.*|.*mobile-app-qa\.html.*|.*company\.html.*|.*link-instructions\.html.*|.*power-search-techniques\.html.*|.*protect-privacy-qa\.html.*|.*what-makes-ixquick-special\.html.*|.*techniques-recherche-puissance\.html.*|.*download-ixquick-toolbar\.html.*|.*instructions-lien-recherche\.html.*|.*parlez-dIxquick-a-vos-amis\.html.*|.*company-background\.html.*|.*company-management-bio\.html.*|.*company-faq\.html.*|.*commentaires\.html.*|.*traffic/.*|.*press-center-recent-coverage\.html.*|.*press-center-company-background\.html.*|.*press-center-logo\.html.*|.*press-center-screenshots\.html.*|.*press-center-contact\.html.*|.*press-center-releases\.html.*|.*do/ipd_optout\.pl.*|.*press-center\.html.*|.*download-ixquick-plugin\.html.*|.*make-ixquick-your-homepage\.html.*).*")  {
    
    /* General and Home page */
    a:hover
    {
    transition: color 0.4s;
    }
    
    b
    {
        color: #eee !important;
    }
    
    body
    {
        background: #222222;
        color: #ddd !important;
        padding-bottom: 50px;
    }
    
        div#content p:first-child img
    {
        display: none;
    }
    
    div#content p:first-child:before
    {
        content: "IxQuick";
        font-size: 250%;
        color: #ddd;
        font-family: serif;
    }
    
    a, #head a
    {
        color: #ddd !important;
    }
    
    #head a:hover
    {
        color: #C5A1A1 !important;
        text-decoration: none !important;
    }
    
    #head a.active
    {
        color: #bbb;
    }
    
    div#light
    {
        display: none !important;
    }
        
    p#caption
    {
        color: #bbb !important;
    }
    
    p#caption span#green
    {
        color: #C5A1A1 !important;
        text-decoration: none !important;
    }
    
    p#caption span#green:hover
    {
        color: #DCBBBB !important;
        text-decoration: none !important;
        transition: color 0.4s;
    }
    
    div#links a, div#links
    {
        color: #bbb !important;
    }
    
    div#links a:hover
    {
        color: #C5A1A1 !important;
        text-decoration: none !important;
    }
    
    div#footer
    {
        color: #bbb !important;
        text-align: center !important;
    }
    
    div#footer a
    {
        color: #bbb !important;
        text-decoration: none !important;
        text-align: center !important;
    }
    
    div#footer a:hover
    {
        color: #C5A1A1 !important;
        text-decoration: none !important;
    }

    div#news_message
    {
        background: none !important;
        border: none;
    }

    div#news_message span#news_message_content a
    {
        color: #C17373 !important;
        text-decoration: none !important;
    }
    
    div#news_message span#news_message_content a:hover
    {
        color: #C5A1A1 !important;
        text-decoration: none !important;
        transition: color 0.4s;
    }
    
    div#news_message span#news_message_content a u
    {
        text-decoration: none !important;
    }
    
    input[src*="/stp_search.gif"]
    {
       width: 0 !important;
       height: 0 !important;
       background: url(http://i.imgur.com/yDcNydr.png) no-repeat!important;
       padding-left: 35px !important;
        padding-top: 34px !important;  
    }
    
    div#lang_div, div#lang_div a, #lang_div table tr td a
    {
        color: #222 !important;
    }
    
    /* Advanced Search */
    a#main_link p#ixquick_logo img
    {
        display: none;
    }
    
    a#main_link p#ixquick_logo
    {
        background: none;
    }
    
    a#main_link p#caption
    {
        display: none;
    }
    
    form#search…form table tbody tr td span, #content table tr td
    {
        color: #eee !important;
    }
    
    form#search…form table tbody tr td span font, #content table tr td font
    {
        color: #eee !important;
        font-weight: bold;
    }
    
    /* Results page */

    div#results_header div#logo.f-l a.ix_logo_transparent
    {
        display: none;
    }

    /*[[adsOptions]]*/

    div#sponsored_container
    {
        background: #353535 !important;
        border: none !important;
    }

    #results, #results .result, #results .result h3, #results .result h3 a
    {
       color: #ddd;   
       text-decoration: none;
    }

	#results .result h3 a
	{
		color: #E9CFCF !important;
	}    

	#results .result h3 a:hover
	{
		text-decoration: underline;
	}

	#results .result p.desc b, div#video_results table tbody tr td b
	{
		font-weight: bold;
		color: #ddd !important;
	}
	
	#results .result p span.url, div#video_results table tbody tr td span.normaltext
	{
		color: #aaa !important;
	}
    
    div#logo a img
    {
        display: none;
    }
    
     div#logo:before
    {
        content: "IxQuick";
        font-size: 250%;
        color: #ddd;
        font-family: serif;
    }
    
    div#logo
    {
        padding-top: 30px;
        margin-top: 30px;
    }

    tr td a
    {
    	color: #DCB7B7 !important;
        text-decoration: none !important;
    }
    
    img[src*="/star.gif"] 
    {
       width: 0px !important;
       /*
       background: url(http://i.imgur.com/1xvF05V.png?2?5226) no-repeat!important;
       */
       background: url(http://i.imgur.com/SzUX00W.png) no-repeat!important;
       padding-left: 15px !important;
       margin-top: 2px !important;
       height: 15px!important;
    }
    
    tr td a.bookmark
    {
        background: #333;
        border: 1px solid #444;
        border-radius: 5px;
        color: #fff !important;
        padding: 10px;
    }
    
    div.no_results_messages_new_ui, span.query_string_on_no_results
    {
        color: #eee;
    }
    
    form#pnform
    {
        color: #C5A1A1;
    }
    
    /* Image Results */
    div#results table tbody tr#imageRow_2, div#results table tbody tr#imageRowDesc_2, div#results table tbody tr#imageRow_4, div#results table tbody tr#imageRowDesc_4, div#results table tbody tr#imageRow_6, div#results table tbody tr#imageRowDesc_6, div#results table tbody tr#imageRow_8, div#results table tbody tr#imageRowDesc_8, div#results table tbody tr#imageRow_10, div#results table tbody tr#imageRowDesc_10, div#results table tbody tr#imageRow_12, div#results table tbody tr#imageRowDesc_12
    {
        background-color: #222 !important;
    }

	div#results table tbody tr[id^="imageRowBorder"]
	{
		display: none;
	}

    
    div#results table tbody tr#imageRow_1, div#results table tbody tr#imageRowDesc_1, div#results table tbody tr#imageRow_3, div#results table tbody tr#imageRowDesc_3, div#results table tbody tr#imageRow_5, div#results table tbody tr#imageRowDesc_5, div#results table tbody tr#imageRow_7, div#results table tbody tr#imageRowDesc_7, div#results table tbody tr#imageRow_9, div#results table tbody tr#imageRowDesc_9, div#results table tbody tr#imageRow_11, div#results table tbody tr#imageRowDesc_11
    {
        background-color: #252525 !important;
    }
    
    div#results table tbody tr[id^="imageRow"] td
    {
        background-color: transparent !important;
    }

    div#results table tbody tr td table tbody tr td span.normaltext:first-child
    {
    	color: #eee !important;
    }

    div#results table tbody tr td table tbody tr td span.reltext
    {
    	color: #888 !important;
    }
    
    div#results table tbody tr td table tbody tr td span.normaltext:last-child
    {
    	color: #ccc !important;
    }
    
    img[src*="/graphics/spacer.gif"] 
    {
        display: none;
    }
    
   
    /* Video results */
    div#results_content div#tip table tbody tr td font, div#results_content div#tip table tbody tr td a.tod
    {
        color: #eee !important;
    }
    
    div#results_content div#tip table tbody tr td font
    {
        display: none;
    }
    
    div#results_content div#tip table tbody tr td span a
    {
        color: #eee !important;
    }
    
    
    div#vsearch_engines ul li a
    {
        color: #eee !important;
    }
    
    div#vsearch_engines ul li a span.checkbox_img
    {
        background: url('http://i.imgur.com/P9N3z6M.png') no-repeat scroll left top transparent;
    }

    div#vsearch_engines ul li a.selected span.checkbox_img
    {
        background: url('http://i.imgur.com/P9N3z6M.png') no-repeat scroll left bottom transparent;
    }
    
    /* Phone Search */
    div#search_form ul:first-child
    {
    	margin-top: 45px;
    }

    div#search_form ul:first-child li
    {
    	color: #eee !important;
    }

    div#search_form div.form-container form p label
    {
        color: #eee !important;
        font-weight: normal;
    }
    
    div#search_form div.form-container
    {
       margin-bottom: 40px;
    }
    
    div.form-container form table div#submit_box div#submit_bg, form#search_form table tbody tr td div#submit_box div#submit_bg
    {
        background: url('http://i.imgur.com/4dLNdIl.png') no-repeat scroll 1px 1px transparent !important;
    }
    
    div#search_form ul li span
    {
        background: url('http://i.imgur.com/P9N3z6M.png') no-repeat scroll 0px 0px transparent;
    }
    
    /* Phone Results */
    td#pagenav table tr td.pagenumbers
    {
        color: #eee;
    }
    
    table tbody tr td table tbody tr td table tbody tr
    {
        background: #222;
    }
    
    table tbody tr td table tbody tr td
    {
        background: #222;
    }
    
    table#categoriestablex tbody tr td a, table tbody td table tbody tr td table tbody tr td a
    {
        color: #eee !important;
    }
    
    table#categoriestablex tbody tr td span, table tbody tr td span#reverse_img_span
    {
        color: #bbb !important;
    }    
    
        img[src*="/graphics/radio_res_C.gif"] 
    {
       width: 0px !important;
       background: url(http://i.imgur.com/knYywv2.png) no-repeat!important;
       padding-left: 15px !important;
       margin-top: 2px !important;
       height: 13px!important;
    }
    
        img[src*="/graphics/radio_res_U.gif"] 
    {
       width: 0px !important;
       background: url(http://i.imgur.com/k9O8Xkj.png) no-repeat!important;
       padding-left: 15px !important;
       margin-top: 2px !important;
       height: 13px!important;
    }
    
    table#reverse_table tbody tr td font
    {
        color: #eee !important;
    }
    
    table tbody tr td font font a img
    {
        background: #bbb !important;
        padding: 4px;
    }
    
    table tbody tr td table tbody tr td.copy
    {
        color: #eee;
    }
}
