css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("radikal.ru") {

body {
	background:#fff !important;
}

.content {
	top:40px !important;
}

/* LIVE GALLERY */

.gallery-show_item {
	background:none;
	width:148px;
	height:156px;
}
.gallery-show_item a {
	display:block;
}
.gallery-show_item img {
	border:0;
	background:#eee;
}
.gallery-show_item div {
	display:none;
}
#scrollDiv .footer_bg-gray {
	display:none;
}

.gallery2_bottom-block .gallery2_preloader5 {
	position:static;
	display:block;
	width:100%;
	overflow:hidden;
	clear:both;
	height:64px;
	background:url(http://radikal.ru/Content/CSS/Basic/pict/preloader_8.gif) 50% 50% no-repeat;
}
.gallery2_preloader5 img {
	display:none;
}

div.gallery2_top-widget {
	top:-5px !important;
}

div#offsetInsideScroll.background_gallery2 {
	box-shadow:none;
  	width:auto !Important;
	margin:0;
}

  .gallery2_left-col_wrapper {
	display:none;
  }
  
  div.test-2 {
  	margin:0 !Important;
	width:auto !Important;
  }

div#leftColumnPointer.gallery2_left-col,
.gallery2_top-block,
.gallery2_bottom-block,
div[style*="width: 95%;"] {
	display:none !important;
}

/* ADS */

.flash,
.banerTop,
.banerBottom,
#left_ban,
#left_ban1,
#left_ban2,
#left_ban3,
a[href*="metrika.yandex"],
a[href*="liveinternet.ru/click"],
.left-a,
.right-a,
.global-link,
.gallery2_left-col_wrapper div[style*="width: 300px;"],
div[data-bind*="advertView"]
{
	width:0 !important;
	height:0 !important;
	overflow:hidden !important;
	display:none !important;
}

}
