  #PanelUI-menu-button .toolbarbutton-icon,
  #PanelUI-menu-button .toolbarbutton-badge-stack {
    visibility: collapse;
  }

  #PanelUI-menu-button::before,
  #PanelUI-menu-button::after {
    display: -moz-box;
  }

  #PanelUI-menu-button:not([open]):hover::before,
  #PanelUI-menu-button:not([open]):hover::after {
    background-image: linear-gradient(rgba(255, 255, 255, .6),
                                      rgba(255, 255, 255, .1));
    background-color: transparent;
    border-color: hsla(210, 54%, 20%, .15) hsla(210, 54%, 20%, .2) hsla(210, 54%, 20%, .25);
    box-shadow: 0 1px hsla(0, 0%, 100%, .3) inset,
                0 1px hsla(210, 54%, 20%, .03),
                0 0 2px hsla(210, 54%, 20%, .1);
  }

  #PanelUI-menu-button[open]::before,
  #PanelUI-menu-button[open]::after {
    background-color: hsla(210, 4%, 10%, .12);
    border-top-color: hsla(210, 4%, 10%, .2);
    box-shadow: 0 1px 0 0 hsla(210, 4%, 10%, .1) inset;
    transition-duration: 10ms;
  }

  #PanelUI-menu-button::before {
    padding: .5em .25em .5em .5em;
  }

  #PanelUI-menu-button::after {
    padding: .5em .5em .5em .25em;
  }

/*[[date-format]]*/

  :root[stylish-month="4"][stylish-date="1"]:-moz-any(
    [stylish-hour="15"],
    [stylish-hour="18"],
    [stylish-hour="21"]) #PanelUI-menu-button::before {
    content: "April";
  }

  :root[stylish-month="4"][stylish-date="1"]:-moz-any(
    [stylish-hour="15"],
    [stylish-hour="18"],
    [stylish-hour="21"]) #PanelUI-menu-button::after {
    content: "Fool's";
  }

  :root[stylish-month="4"][stylish-date="1"]:-moz-any(
    [stylish-hour="0"],
    [stylish-hour="3"],
    [stylish-hour="6"],
    [stylish-hour="9"],
    [stylish-hour="12"]) #PanelUI-menu-button::before {
    content: "INVALID";
  }

  :root[stylish-month="4"][stylish-date="1"]:-moz-any(
    [stylish-hour="0"],
    [stylish-hour="3"],
    [stylish-hour="6"],
    [stylish-hour="9"],
    [stylish-hour="12"]) #PanelUI-menu-button::after {
    content: "DATE!";
  }
}

