css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tomshardware.com")

{
    /* default */
    *
    {
        color: #ddd !important;
        box-shadow: none !important;
    }
    
    /* make everything dark except image overlays */
    * :not(.overlay)
    {
        background: #222 !important;
    }
    
    /* remove unnecessary borders */
    section, div.line, .ob-rec-link-img, .blocLatestArticles .carousel-wrap li img,
    .imgThumb, .tagsElm, .chartButton, .pager, .results, .box, .choice, .topPromo,
    .arrow-box-bottom-left, .summary, .edtPagerLz, .loaded, .leftCol,   .tagsList,
    .tagToFollow, .icon-follow:before, .responseContent, .responseText
    {
        border: 0px solid #222 !important;
    }
    
    .reviews .nav1 .choice
    {
        border: 1px solid #ddd !important;
    }
    
    .tagsElm, .tagBtn, .txtTag
    {
        background: #333 !important;
    }
    
    .breadcrumbItem
    {
        background: #3d3d3d !important;
    }
    
    .breadcrumbItem a
    {
        background: none !important;
    }
    
    .breadcrumbItem:hover, .breadcrumbItem a:hover
    {
        background: #171717 !important;
    }
    
    select, input, span, code, textarea, pre, label, div, dd, .item-container
    {
        color: #ddd !important;
    }
    
    a
    {
       color: #ee9 !important;
    }
        
    .btMed, .btPrim, .btBig, .btOr, .ui-prim, .ui-prim:visited, .nb
    {
        color: #ddd !important;
        background-color: #A9200D !important;
    }

    a:visited
    {
       color: #cc8 !important;
    }
    
    .topContainer
    {
       background: #ddd !important;
    }
    
    /* disable floating "share"-bar (annoys me because it sometimes reloads after scrolling) */
    #shareBarFloating, .inner20, .socialPromoteBox
    {
       display:none;
    }

}
