css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(mangareader.net)
{
#top_navigation, .secnav-first, .secnav, .secnav-last
{
	display: /*[[menu]]*/ !important;
}
.logo
{
	display: /*[[logo]]*/ !important;
}
#admaintop, .c7, .c9, .pageSkinLeft, .pageSkinRight
{
	display: /*[[front]]*/ !important;
}
#adtop, #adfooter, .content-r-ad, .content-l-ad
{
	display: /*[[ads]]*/ !important;
}
#mangainfo
{
	display: /*[[name]]*/ !important;
}
#topchapter
{
	display: /*[[nav]]*/ !important;
}
.zoombottom, #onlinemanga, #tfollowme
{
	display: none !important;
}
#bottomchapter, #wrapper_footer
{
	display: /*[[bottom]]*/ !important;
}
.aftersearch
{
	display: /*[[search]]*/ !important;
}
.top, #topchapter
{
	width: 800px !important;
}
.zoomtop
{
	margin-bottom: 5px !important;
}
.top, #wrapper_header, #topchapter
{
	height: auto !important;
	border: none !important;
	margin: 0 auto !important;
}
.top, #topchapter, .zoomtop, .zoombottom, #bottomchapter, #wrapper_footer, #adtop, #adfooter, .content-r-ad, .content-l-ad
{
	display: /*[[read]]*/ !important;
}
#imgholder
{
	position: /*[[border]]*/ !important;
	left: 0px !important;
	top: 0px !important;
}
#img
{
	width: /*[[larg]]*/px !important;
	height: /*[[haut]]*/px !important;	
}
.imageinside, .latesthotimages > .imageinside > a > img
{
	height: 100px !important;
	width: 80px !important;
	margin-left: auto !important;
	margin-right: auto !important;
}
.latesthotimages
{
	height: 140px !important;
}
#latestchapters, .updates, .c6
{
	width: 400px !important;
}
#wrapper_body
{
	width: 800px !important;
}
.c3
{
	position: relative !important;
	bottom: 100px !important;
}
#mangainfo
	{
		font-size: 8px !important;
		position: absolute !important;
		margin-top: 3px !important;
		margin-left: 115px !important;
		overflow: hidden !important;
		width: 400px !important;
		white-space: nowrap !important;
	}
#chapterMenu
	{
		width: 108px !important;
	}
#sec_navigation
	{
		width: 225px !important;
	}	
.aftersearch
	{
		margin-left: 0px !important;
	}
#sec_navigation > ul
	{
		padding-left: 0px !important;
	}
.ac_results
	{
		width: 222px !important;
	}
.ac_results > ul
	{
		overflow: hidden !important;
	}
.ac_results > ul > li > div
	{
		width: auto !important;
	}
}
