@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("flickr.com") {

.photo-stats-view {background: rgb(200,200,200); margin:0px 0px 2px}
.context-slider-view {background: rgb(200,200,200)}
.photo-sidebar-actions-view {background: rgb(200,200,200); max-width: 100%}
.photo-charms-view .photo-charms-bar {background: rgb(200,200,200); margin:0}
.attribution-view .attribution-info .owner-name {color: rgb(5,5,5)}
.comments-view .comments .comment .comment-author a {color: rgb(0,0,0)}
.photo-sidebar-view a {color: rgb(5,5,5);}
.view.photo-sidebar-view {background: rgb(255,255,255)}
.view.photo-sidebar-view {color: rgb(5,5,5)}
.view.photo-well-view {background: rgb(200,200,200)}
.comments-view .comments-form .comment-form-field textarea {background: rgb(225, 225, 225)}
.comments-view .comments-more a {color: rgb(255, 165, 0)}
.photo-sidebar-view .newCloseBtn {background: url("https://s.yimg.com/uy/build/images/../images/icons-2x-s058b13a5db.png") no-repeat scroll 0px -139px / 191px auto rgb(200, 200, 200)}

}
