/* AGENT_SHEET */
@-moz-document domain("symfony.com") {
    
    /* Comments */
.highlight pre span.c1, .highlight pre span.sd, pre span.c
    {
        color: #ccaa99 !important;
    }

    /* Strings */
.highlight pre span.s1, .highlight pre span.s2, .highlight pre span.s, .highlight  pre span.se, pre span.sb
    {
        color: #33D934 !important;
    }
    
    /* Keywords */
 pre span.k, .highlight pre span.p-Indicator, pre span.kd
    {
        color: #D88718 !important;
    }
    
    /* =, ==, ->, :: … */
  pre span.o, pre span.ni
    {
        color: #D36342 !important;
    }
    
    
    /* Yml and Class */
 pre span.nc, pre span.l-Scalar-Plain
    {
        color: #eee !important;
    }
    
    /* Numbers */
 pre span.mf, pre span.mo, pre span.mi, pre span.m
    {
        color: #DD5A80 !important;
    }
    
    /* Line numbers, brackets and punctuation */
    pre span.p, pre span.cp, td.linenos pre
    {
        color: #aaa !important;
    }
    
    /* HTML */
    pre span.nt
    {
        color: #d0d0d0 !important;
    }
    
    /* Internal PHP Functions */
    pre span.nb
    {
        color: #B2DFF0 !important;
    }
    
    /* Methods */
     pre span.na
    {
        color: #9ED62D !important;
    }

	/* Variables & Everything else */
    pre span.nv, pre span.nx, pre span.nf, pre
    {
        color: #eee !important;
    }
}
