css:
@-moz-document url-prefix("https://volafile.io/") {
html {
	background-color: #1e2d37 !important;
}

#header {
	background-color: #1e2d37;
 	border-color: transparent;
}

.header_row_element, #header_row1, #header_row2, #room_name_container {
	border: none;
}

#chat_messages {
	background-color: #25323b;  
}
    
#chat_messages_frame {
    border: none;
}

#chat_input {
	background-color: #25323b;
  	border: none;
}

#chat_hbar {
  	background-color: #293e4c;
}

#chat_name {
 	background-color: #293e4c; 
}

#upload_container .button {
	background-color: #4d8fd1;
}

.ui_frame_buttons .button {
	background-color: #4d8fd1;
}
  
.ui_frame_buttons .button:hover {
	background-color: #62a6eb;
}

.ui_frame_buttons .button:active {
	background-color: #84bef8;
}

table { 
	border-collapse:collapse; 
  	border: none;
}
  
.chat_file {
	/*background-color: #2d688e;*/
    /*background-color: #656f84;*/
  	background-color: #33414a;
    border-color: white;
}
    
#chat_messages .chat_message.user a.username { /* Regular user */
	color: #3493eb;
}
  
#chat_messages .chat_message.rank a.username { /* Homosexuals */
 	color: #997ffb;
}
  
#chat_messages .chat_message a.username { /* Unregistered user */
	color: white;
}

#chat_messages .chat_message.admin a.username { /* Admin/system */
	color: #d49418;
}

#chat_messages .chat_message.donator a.username { /* Donators */
  	color: #53a1e9;
}

/*
#chat_messages .chat_message.self a.username {
  	color: #dd4343;
}
*/

#file_list.uneven .filelist_file:nth-child(2n+1) { /* First color. */
  	background-color: #1b2f3d;
}

#file_list.even .filelist_file:nth-child(2n) { /* Second color. */
  	/* background-color: #264256; Deprecated*/
  	background-color: #284152;
  	
}

#file_list.even .filelist_file:nth-child(2n-1){ /* First color. */
	background-color: #1b2f3d;
  	
}

#file_list.uneven .filelist_file:nth-child(2n+2){ /* Second color */
	/*background-color: #2a3a46; Deprecated*/
  	background-color: #284152;
  	
}

/*
I have no fucking clue how the above thing handles the file list colors. it just werks

*/

.file_right_part .hover_show{
    display:inline !important;
}

.file_right_part .hover_show:after{
    content: ' - ';
}

.file_right_part:hover .hover_hide{
    display:inline !important;
}

#gallery_frame {
	background-color: rgba(0,0,0,.68);
}

.ui_frame {
	background-color: #1e2d37;
  	border-color: #275371;
}
    
.pretty_table, .ui_frame_content {
	border-collapse: collapse;
  	border: none;
    outline: none;
}

.pretty_table td {
  	border-collapse: collapse;
    border: none;
    outline: none;
}

.dropdown:hover, a.nodecoration:hover {
	background-color: #2d4556;
}

.dropdown_list {
	background-color: #2d4556;
    border-color: #275371;
}
      
#filter_reminder, #search_input {
	background-color: #264559;
}
      
#filter_reminder:hover {
	background-color: #3d5c70;
}

#radio_container {
	background-color: #1e2d37;
}

#radio_current {
	background-color: #284152;
  	border-color: #1e2d37;
}

#top_navigation, .top_nav_item, #top_navigation_contents {
	background-color: #1e2d37;
  	border-color: #27465a;
  	border-right-color: #27465a;
}

.top_nav_item[href='/donate'] {
	border-right-color: #27465a;
}

.top_nav_item:hover {
	background-color: #3d5c70;
}

#user_toplist th {
	background-color: #2d4556;
}

tbody td {
	border-color: #3d5c70;
}

.button.light {
	background-color: #2d4556;
}
    
.button.light:visited {
	background-color: #2d4556;
}

#radio_load_progress {
	background-color: #29485c;
}

#radio_play_progress {
	background-color: #3f647d;
}

#radio_progress {
 	background-color: #1e2d37; 
}
}
