@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("darklyrics.com") 
{




/*Ben's Hovered HREF Links Color Changing Code:*/
a:hover, a:hover > font, a > font:hover, a[href]:hover
{
  color:#FF0000 !important;
}

/*Ben's Hovered HREF Links Color Changing Code:*/
a:visited
{
  color:#ff9933 !important;
}

/*Ben's Regular HREF Links Color Changing Code:*/
a
{

  color:#0000FF !important;
}

a > font
{
  color: #000000 !important;
}


html, table, tr, p, body
{
  background-color: #FFFFFF !important;
  color: #000000 !important;
  font-family: Calibri,Arial !important;
  font-size: 12px !important;
  border-color: black !important; 
}

td,b,p
{
  color: #000000 !important;
}


FONT
{
  color: #000000;
  background-color: #FFFFFF !important;
}

FONT
{
  color: #000000;
  background-color: #FFFFFF;
}

font[size="4"][face="Helvetica"][color="#ffffcc"]
{
  color: #000000 !important ;
}

font [size="5"][color="#ffffcc"]
{
  color: #000000 !important;
}


}
