css:
@-moz-document url-prefix("http://www.azlyrics.com/lyrics/") {
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 300;
  src: local('Open Sans Light'), local('OpenSans-Light'), url(http://themes.googleusercontent.com/static/fonts/opensans/v8/DXI1ORHCpsQm3Vp6mXoaTaRDOzjiPcYnFooOUGCOsRk.woff) format('woff');
}
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 700;
  src: local('Open Sans Bold'), local('OpenSans-Bold'), url(http://themes.googleusercontent.com/static/fonts/opensans/v8/k3k702ZOKiLJc3WVjuplzKRDOzjiPcYnFooOUGCOsRk.woff) format('woff');
}
#h1head, .noprint, .smallfont, .copyright, .ringtone, h2 {
  display: none !important;
}

body, #main{
  background-color: #f8f8f8;
  text-align: center;
  margin: 0;
  padding: 0;
  color: #302c22;
}

#main{
  border: none;
  margin: 50px auto 10px;
}

#main b {
  font-family: 'Open Sans', san-serif !important;
  font-size: 36px;
  font-style: normal;
  font-variant: normal;
  font-weight: 700;
  display: inline-block;
  margin-bottom: 20px;
}

#main div {
  font-family: 'Open Sans', san-serif !important;
  font-size: 21px;
  font-style: normal;
  font-variant: normal;
  font-weight: 300;
  line-height: 36px;
}
}
