tabs.tabbrowser-tabs#tabbrowser#tabs{
	 background-color: transparent!important;
	}

.tabbrowser-tab, .tabs-newtab-button {
	 background: transparent !important;
	 color: #ffffff !important;
	 text-shadow: #000000 2px 0px 2px !important;
	 border: #000000 1px solid !important;
	 padding-left: 4px !important;
	 padding-right: 4px !important;
	-moz-box-shadow: inset 0px 0px 1em 0px rgba(255,255,255,.5) !important; 
	-moz-border-radius: 6px 6px 0px 0px !important;
	 height: 25px !important;
	}
	
.tabbrowser-tab:not([selected="true"]){
	 margin-left: 1px !important;
	 margin-right: 1px !important;
	 font-size: 1.1em !important;
	 color: #ffffff !important;
	 margin-bottom: 6px !important;
     padding:0 !important;

	}

.tabbrowser-tab[selected="true"]{
	 background: transparent !important;
	 font-size: 1.3em !important;
	 color: #fff988 !important;
	 text-shadow: #000000 0px 0px 2px !important;
    -moz-box-shadow: inset 0px 0px 10px 0px #81F0F6 !important;
	}
	
.tabbrowser-tab:hover{
	background: transparent !important;
	color: #81F0mm !important;
	text-shadow: #555444 0px 0px 2px !important;
	-moz-box-shadow: inset 0px 0px 15px 0px #81F0F6 !important;
	}


#TabsToolbar, #browser-bottombox, #status-bar, #status-bar > *, #nav-bar, #PersonalToolbar, #addon-bar {
 	 background: transparent !important;
	 color: #ffffff !important;
	-moz-appearance: none !important;
	 border: #000000 !important;
	}
	
.chromeclass-directories *, #bookmarksPanel * {
         color: #ffffff !important;
         text-shadow: #81F0F6 0px 0px 2px !important;    
}

tooltip {
  -moz-appearance: none !important;
  border: none !important;
  color: #ffffff !important;
  text-shadow: #81F0F6 0px 0px 2px !important;
  background-color: rgba(0,0,0,0.20) !important;
  -moz-border-radius-topleft:7px;
  -moz-border-radius-topright:7px;
  -moz-border-radius-bottomleft:7px;
  -moz-border-radius-bottomright:7px;

}




















