css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.google.com/search"), url-prefix(http://www.google.), url-prefix(http://images.google) {

  #wikibox { font-family: tahoma, verdana, san serif, arial !important; border:1px solid #36C !important; padding-bottom:10px !important; padding-right:4px !important;}
  #wikibox:hover {}

  #wikibox body, #wikibox font, #wikibox td, #wikibox div { font-size:80% !important;}
  #wikibox a:link { color:#646464 !important;} 
  #wikibox .shrunk, #wikibox .ra, #wikibox #tpa1, #wikibox .ta  { display:none !important;}

  .firstHeading {padding-top:30px !important;}

  .printfooter { position:absolute !important; top:20px !important; width:97% !important; display:block !important; background-color:#EFEFEF !important;}

  #bodyContent { padding-top:0px !important;}

  div.g, li.g { border:1px solid #bac5ba !important; border-bottom:1px solid #A2B2A2 !important; padding:2px !important;}
  div.g:hover, li.g:hover { border:1px solid #36C !important; background-color:#D5DFF3 !important;}

  .j { width:100% !important; display:block !important;}

  #gdsf { font-size:90% !important;}
}
