css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("search.yacy.net") {
    body, html {
       background-color: #222;
       color: #aaaaaa;
       font-size: 12px;
       border: 0px solid #333333;
    }
    
    /* General */
    .btn-default, .btn {
       color: #eeeeee;
       background-color: #444444;
       border: 1px solid #505050;
    }
    .btn-default:hover, .btn:hover, .btn-default.active, .btn.active {
       color: #eeeeee;
       background-color: #305189;
       border: 1px solid #206199;
      color: #ffffff;
    }
    #content, #graph {
       background-color: #121212;
       border: 0px solid #333333;
       padding: 2px;
        margin: 20px 0px;
    }
    #tagcloud {
        margin: 15px 0px;
    }

    /* Links */
    a:link {
        color: #888888;
        background-color: transparent;
        text-decoration: none;
    }
    a:link:hover {
       color: #70b1e9;
       background-color: transparent;
       text-decoration: none;
    }
    
    /* Dropdown Menu */
    a.dropdown-toggle {
       color: #cccccc;
    }
    a.dropdown-toggle:hover {
       color: #eeeeee !important;
    }
    .dropdown-menu {
       color: #aaaaaa;
       background-color: #444444;
    }
    .dropdown-menu > li > a {
       color: #cccccc;
    }
    .dropdown-menu > li:hover > a {
       color: #ffffff;
       background-color: #206199;
    }
    .dropdown-menu > a {
       color: #cccccc;
       background-color: #206199;
    }
    .dropdown-menu > li > .active {
       color: #cccccc;
       background-color: #206199;
    }
    .divider {
       background-color: #222222 !important;
    }
    
    /* Headers */
    h1 {
        font-size: 200%;
        color: #cccccc;
    }
    h2 {
        font-size: 180%;
        color: #cccccc;
        padding: 15px 5px;
    }
    h3 {
        font-size: 160%;
        color: #cccccc;
    }
    h4 {
        font-size: 140%;
        color: #cccccc;
    }
    .favicon{
        margin-top: 3px !important;
    }
    
    /* Input */
    input, .form-control {
       margin: 0px 0.2em;
       color: #cccccc;
       background: #181818;
       border-radius: 5px;
       padding: 4px 12px;
       border: 1px solid #333333;
       font-size: 140%;
    }
    input[type="checkbox"], input[type="radio"] {
       -moz-box-sizing: border-box;
       padding: 0px;
    }
    fieldset.maininput, fieldset.yacys {
       background-color: #2c2c2c;
       border-radius: 1px;
       color: #cccccc;
    }

    /* Yacy Main Search */
    .searchinput {
       font-size: 150%;
        color: #aaaaaa;
    }
    .yacysearch  {
       color: #cccccc;
        font-size: 120%;
    }
    dl {
       display: inline-block;
       width: 96%;
       background-color: #282828;
    }
    form dt, dl.pairs dt {
       background-color: #282828;
        color: #cccccc;
       font-weight: bold;
       font-size: 120%;
    }
    form dd, dl.pairs dd {
       background-color: #282828;
       color: #888888;
       padding-left: 10px;
    }
    
    /* Navigation Bar */
    .navbar-fixed-top {
        border: 0px solid #555555;
    }
    .navbar-default {
        background-color: #333333;
        border-color: #444444;
    }
    .sidebar {
       background-color: #282828;
    }
    #search {
       margin: 0px 0.2em;
       color: #cccccc;
       background: #181818;
       border-radius: 5px;
       padding: 5px;
       border: 1px solid #105189;
       border-top-right-radius: 0px;
       border-bottom-right-radius: 0px;
    }
    #Enter {
       font-weight: bold;
       margin-top: -2px;
       color: #cccccc;
       background-color: #105189;
       border-radius: 5px;
       padding: 6px 12px 6px 12px;
       border: 1px solid #206199;
       border-top-left-radius: 0px;
       border-bottom-left-radius: 0px;
    }
    #Enter:hover {
       border: 1px solid #406199;
       background-color: #305189;
       color: #eeeeee;
       margin-left: 2px;
    }
    .tt-dropdown-menu {
       border: 1px solid #3071a9;
       background-color: #204189;
       border-radius: 5px;
       color: #cccccc;
    }
    
    /* Sidebar */
    .menugroup h3 {
       border-top-left-radius: 5px;
       border-top-right-radius: 5px;
       background-color: #444444;
       border: 1px solid #484848;
       padding: 3px;
       margin: 0px 15px;
       color: #FFF;
       font-size: 130%;
    }
    .MenuItemLink, td {
      background-color: #303030 !important;
      margin: 0px 15px;
      border: 0px solid #333333;
    }
    .menugroup iframe {
      height: 110px;
      padding: 0px;
      margin: 0px 15px;
      background-color: #444444 !important;
    }
    
    /*  Progress Bar */
    b, strong {
      font-weight: 400;
      color: #cccccc;
    }
    .progress {
      height: 20px;
      margin-top: 1px;
      margin-bottom: 16px;
      border: 1px solid #333333;
      overflow: hidden;
      background-color: #222222;
      border-radius: 3px;
      color: #cccccc;
      box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.1) inset;
    }
    .progress-bar {
        float: left;
        width: 0px;
        height: 100%;
        font-size: 12px;
        line-height: 20px;
        color: #222222;
        text-align: center;
        border: 1px solid #333333;
        background-color: #105179;
        box-shadow: 0px -1px 0px rgba(0, 0, 0, 0.25) inset;
        transition: width 0.6s ease 0s;
    }
    #progressbar > span {
      color: #888888 !important;
      margin: -4px;
    }
    
    /* Tabs */
    #sort_button_date {   
    }
    .SubMenu > h3 {
       margin: 4px !important;
       background-color: #333333;
       border: 1px solid #484848;
       border-radius: 3px;
       padding: 5px !important;
       font-size: 130% !important;
    }
    .SubMenugroup {
       margin: 4px !important;
       background-color: #303030;
       border-top-left-radius: 3px;
       border-top-right-radius: 3px;
    }
    .SubMenugroup h3, legend {
       color: #999999;
       background-color: #444444;
       border-top-left-radius: 3px;
       border-top-right-radius: 3px;
       border-bottom-left-radius: 0px;
       border-bottom-right-radius: 0px;
       padding: 2px 5px !important;
    }
    #Crawler fieldset {
       margin: 4px !important;
       background-color: #333333;
       border: 0px solid #383838;
       border-radius: 3px;
       padding: 5px !important;
       color: #aaaaaa;
    }
    fieldset > dl {
      margin-top: -5px !important;
    }
    
    /* Search Results */
    b {
        color: #888888 !important
    }
    .searchresults h4 a:link {
        color: #aaaaaa;
    }
    .searchresults h4 a:link:hover {
        color: #3071a9;
    }
    .searchresults .url a {
       color: #666666;
    }
    .searchresults .url a:link:hover {
       color: #3071a9;
    }
    .urlinfo {
       color: #444444;
    }
    .snippetLoaded {
       color: #888888;
    }
    
    /* Pagination */
    .pagination > li > a, .pagination > li > span {
        position: relative;
        float: left;
        padding: 6px 12px;
        margin-left: -1px;
        color: #888888;
        text-decoration: none;
        background-color: #333333;
        border: 1px solid #444444;
    }
    .pagination > li.disabled > a, .pagination > li.disabled > span, .pagination > li.disabled > a:hover, .pagination > li.disabled > a:hover > span {
        position: relative;
        float: left;
        padding: 6px 12px;
        margin-left: -1px;
        color: #222222;
        text-decoration: none;
        background-color: #333333;
        border: 1px solid #444444;
    }
    .pagination > li.active > a, .pagination > li.active > span {
        background-color: #444444;
        border: 1px solid #3071a9;
        color: #80c1f9;
        font-weight: bold;
    }
    .pagination > li:hover > a, .pagination > li:hover > span  {
        background-color: #206199;
        border: 1px solid #3071a9;
        color: #eeeeee;
        font-weight: bold;
    }
}
