/* Created by miilofon on 2 April 2014 in Siberia */

/* Pictures */
@-moz-document domain("youtube.com") 
{body, .comment-settings-header {background: url(http://i.imgur.com/NPg5gVI.jpg) !important;}
#watch-action-panels, .branded-page-v2-container, #watch-discussion, #action-panel-details, #watch-header, #guide-container, #appbar-settings-menu, #yt-picker-language-footer, #yt-picker-country-footer, #yt-picker-safetymode-footer, #masthead-appbar, .yt-uix-button-menu, #masthead-expanded-container, .yt-uix-shelfslider-prev, .yt-uix-shelfslider-next,
#yt-masthead-container, #watch7-sidebar, #footer-container, .GKY1I5JBMEB {background: url(http://i.imgur.com/oU9PuVW.jpg)!important;}
#watch7-container {background: url(http://i.imgur.com/wIrqu52.jpg)!important;}
/* .logo, .footer-logo-icon {background: url(http://i.imgur.com/62xDCDg.png)!important; background-repeat: no-repeat !important;} */
/* .logo, .footer-logo-icon {background: url(http://i.imgur.com/OQjsqje.png)!important; background-repeat: no-repeat !important;} */
.logo, .footer-logo-icon {background: url(http://i.imgur.com/WatzuoE.png)!important; background-repeat: no-repeat !important;}
.yt-uix-button-subscribe-branded { background: url(http://i.imgur.com/ZrrAXpd.jpg) !important; border-radius: 6px 0px 0px 6px !important;}

/* Settings */
.logo {margin-left: 8px !important;}
#guide-container {box-shadow: 0 4px 4px 2px rgba(0,0,0,0.1);}
#appbar-settings-menu, #yt-picker-language-footer, #yt-picker-country-footer, #yt-picker-safetymode-footer, #content, 
#masthead-appbar, .yt-uix-button-menu, #masthead-expanded-container {box-shadow: 0 0 4px 2px rgba(0,0,0,0.1);}
.yt-uix-shelfslider-prev, .yt-uix-shelfslider-next {border-color: #C3C3C3 !important;}
#masthead-appbar-container {box-shadow: 0 3px 4px  rgba(50,50,50,0.2);}
#help, .yt-card, .ytg-fl, #non-appbar-vm-video-actions-bar, #vm-pagination, #analyticsCard, .vm-video-item, .all-comments, .all-comments > a:nth-child(1) {background: none !important;}
.branded-page-v2-detached-top .branded-page-v2-container {margin-top:0px !important;}
.branded-page-v2-secondary-col, #footer-logo img, .ab-midpane, #yt-comments-sb-standin .callout, #watch7-sidebar-ads, #header {display :none !important;}
#yt-masthead-container {box-shadow: 0 2px 2px rgba(50,50,50,0.2);}
#footer-logo { width: 72px; height: 30px; vertical-align: middle; }
#guide {border-right: 1px solid #E8E8E8 !important;}
#watch7-container, .branded-page-v2-container {box-shadow: 0 0 6px rgba(0,0,0,0.1);}
#watch-discussion, #action-panel-details, #watch-header, #watch-action-panels {box-shadow: 0 0 6px rgba(0,0,0,0.3) !important;}
.comments .comment-item .user-photo, .share-service-button {box-shadow: 0 0 2px rgba(0,0,0,0.6) !important;}
.thumb-wrapper, .yt-mix-thumb, .yt-pl-thumb, .pl-header-thumb, .pl-video-thumb {box-shadow: 0 0 2px rgba(0,0,0,0.8) !important;}
.yt-lockup-thumbnail, .yt-thumb video-thumb {box-shadow: 0 0 2px rgba(0,0,0,0.8) !important; top: +2px !important; left: +2px !important;}
#watch-discussion, #action-panel-details, #watch-header, #watch7-sidebar, #watch7-container, #content, .branded-page-v2-container, #watch-action-panels {border-radius: 7px !important;}   
#watch7-sidebar {padding-bottom: 16px; box-shadow: 0 0 3px 3px rgba(0,0,0,0.1); }
#watch7-headline, #watch7-user-header, #guide, #content .primary-col .branded-page-v2-body, #feed, 
#content .branded-page-v2-primary-col, #branded-page-body .tab-content-body, .secondary-header-contents {background: transparent !important;}
  
#footer-container {box-shadow: 0 0 6px rgba(50,50,50,0.25);}
#watch-description, .comments, #watch7-sidebar-contents {box-shadow: 0 24px 20px -14px rgba(160,160,160,0.2);}  
#watch-description {padding-bottom: 8px !important;}
#watch7-action-panel-footer {background: transparent !important; height: 1px; }
#watch7-headline, #watch7-user-header {border: none;}
.yt-uix-scroller, .yt-ui-ellipsis-2, .yt-uix-expander, .yt-ui-ellipsis, .comments .paginator {background: transparent !important; border: none !important; }
#yt-comments-sb-standin .box {background: #F8F8F8 !important;}
#yt-comments-order-button {background: #F5F5F5 !important;}
.box {border-radius: 5px !important;}  
  
/* Large Player */
html .watch-stage-mode .player-width, html .watch-stage-mode #player {width: 100% !important; width: calc(100vw - 18px) !important; /* 18px for the scrollbar */ margin-left: 0 !important; left: 0 !important; }
html .watch-stage-mode #placeholder-player .player-width {width: 100% !important;}
html .watch-stage-mode .player-height {height: 95vh !important; height: calc(100vh - 52px - 18px) !important; /* YouTube top bar && scrollbar. */ }
html .watch-stage-mode .ad-container, html .watch-stage-mode .html5-video-container, html .watch-stage-mode video.video-stream.html5-main-video {left: 0 !important; top: 0 !important; width: 100% !important; height: 100% !important;}
html .watch-stage-mode .html5-video-container > .video-ads > .ad-container {/* Fix for ads bottom bar at the old player. */bottom: 30px !important; height: auto !important;}
html .watch-stage-mode .ad-container.ad-overlay {/* Fix for banner-style ads. */ top: auto !important; height: auto !important;}
html .watch-stage-mode #player-playlist .watch-playlist { /* Fixing the playlist sidebar (when watching a video from a playlist). */ top: calc(100vh - 52px - 18px - 360px) !important; height: 480px !important;}
html .watch-stage-mode .ytp-chrome-bottom { /* Video control bar for the new player. */ width: 95% !important; width: calc(100% - 24px) !important;}
#masthead-positioner {position: absolute !important;}
#movie_player > .html5-video-controls > .html5-player-chrome {/* Making the old player look similar to the new player. */ background: linear-gradient(to bottom, rgba(27,27,27,0) 0%, rgba(27,27,27,0.5) 100%); } 
#theater-background {display :none !important;} /* <--- my improvement... */
}
/* More */
@-moz-document domain("youtube.com"),
/* url-prefix('https://plus.googleapis.com/_/im/_/widget/render/comments'), */
/* url-prefix('https://apis.google.com/_/im/_/widget/render/comments'), */
/* url-prefix('https://apis.google.com/u/0/wm/4/_/widget/render/comments'), */
/* url-prefix('https://apis.google.com/u/0/_/widget/render/comments')  */
url-prefix('https://plus.googleapis.com'),
url-prefix('https://apis.google.com') {
.yJa, .Mza, .uPa, .DJa /* Google Plus Comments */ {background: transparent !important;}
.RN {background: transparent !important; border-color: #C3C3C3 !important;}
.Mga, .Jea {display: none !important;}}
