css:
@-moz-document domain('facebook.com') {

/* Remove this line to use webfonts. Commented out because userstyles.org does not allow @imports.

	@import url('https://fonts.googleapis.com/css?family=Open+Sans:300italic,400italic,600italic,700italic,800italic,400,300,600,700,800');

	@import url('https://fonts.googleapis.com/css?family=Ubuntu:300,400,500,700,300italic,400italic,500italic,700italic');

Remove this line to use webfonts. Commented out because userstyles.org does not allow @imports. */

	div#rightCol {
		display: none;
		}

	div#globalContainer {
		width: auto !important;
		margin: 0 20px;
		}

	div#content, div#contentArea {
		width: 100% !important;
		}

	html, body, div, p, span {
		font-family: 'Open Sans', Helvetica, Helvetica Neue, Arial;
		-webkit-text-stroke: 0.20px;
		}

	a, a[role='button'],
	input[type='button'],
	textarea.textInput{
	  font-family: 'Ubuntu', Helvetica, Helvetica Neue, Calibri, Arial !important;
	  font-weight: 500 !important;
	  -webkit-text-stroke: 0.20px;
	}

	div.tickerFeedMessage,
	div.mtm div.lfloat {
		font-family: 'Open Sans', Helvetica, Helvetica Neue, Calibri, Arial !important;
		}

	p {
		font-size: 12px;
		}

	div#leftCol {
		font-size: 11px;

		}

	div.userContentWrapper {
		padding: 5px;
		}

	div.UFICommentContentBlock {
		padding: 0 3px;
		}

	li.UFIRow.UFIComment {
		padding: 6px 0;
	}

	/* Hide Suggested Posts */
	div[data-ft*="ei"] {
	  display: none;
	}

	div.mtm div.clearfix div {
	  font-family: 'Open Sans', Helvetica, Helvetica Neue, Arial !important;
	color: #333;
	}

	/* If you don't like the light grey background & gutters, use this to change them. */
	body, #contentCol {
	  /* background-color: #fff !important; */
	}
	
}
