css:
@-moz-document url("about:newtab"){

#newtab-window 
{ background: url(http://www.wallsave.com/wallpapers/1366x768/anime/499418/anime-girl-499418.jpg) !important; 
background-size: cover !important; }

#newtab-scrollbox:not([page-disabled])
{ background: transparent !important; }

#newtab-grid
{ background: rgba(10,10,10, .4) !important;
border: 3px double black !important;
padding: 20px !important;
min-width: 960px !important;
min-height: 760px !important; }

.newtab-site
{ opacity: .85 !important;
border: 3px double black !important;
box-shadow: 4px 4px 8px rgba(0,0,0,.6) !important;
background: radial-gradient(at top,#A4AA9C, transparent) !important;
-moz-transition: .5s ease-in all !important; }

.newtab-site:hover
{ -moz-transform: scale(1.5,1.5) !important;
z-index: 999999 !important;
opacity: 1 !important;
transition: 5s opacity ease-in-out !important;
-moz-transition: .5s ease-in all !important; }

.newtab-title
{ background: radial-gradient(at top,#A4AA9C, transparent) !important;
border: none !important;
padding-top: 2px !important;
color: black !important;
font-family: "Segoe UI" /* Estrangelo Edessa*/ /*Rockwell*/, sans-serif !important;
text-align: center !important;
font-weight: bold !important; }

 .site-strip:hover, .site-strip:active, .site:hover .site-strip 
{ background: radial-gradient(at top,rgba(164,170,156, .8), transparent) !important; }

/* hover close button */
input[title="Remove this site"]:hover,
#newtab-toggle:hover
{ opacity: 1 !important; }

/* close tab button */
input[title="Remove this site"]
{ opacity: .65 !important;
background: url(data:image/png;base64,) no-repeat !important; }

#newtab-toggle
{ opacity: .65 !important;
width: 32px !important;
height: 32px !important;
background: url(data:image/png;base64,) no-repeat !important; } }
