css:
@-moz-document domain("thesandtrap.com"){

/** Announcement Text **/
.ipsType_richText {
    color: #ccc;}
/** Main Post Font **/
body, div, dl, dt, dd, ul, ol, li, h1, h2, h3, h4, h5, h6, pre, code, form, fieldset, legend, input, button, textarea, p, blockquote {
	color: #ccc;
	background: #1a1a1a}
/** Topic Title In Topic **/
.ipsType_pageTitle {
    color: #ccc;}
/** Member Profile Box Border **/
aside.cAuthorPane {
    border-right: 2px solid #282828;
    background-color: #282828;}
/** Something Box **/
element.style {
    background: #1a1a1a;}
/** Member Box Background **/
.ipsList_inline, .ipsList_reset {
    background: #282828;}
/** Topic Title **/
html[dir] .ipsContained_container > span.ipsContained {
    background: #1a1a1a;}
/** Top Box **/
element.style {
    background: #1a1a1a;}
/** Title Background **/
.ipsPageHeader {
    background: #1a1a1a;}
/** Rating and Top of Post **/
.ipsType_light {
    background: #1a1a1a;}
/** New Content Font Color Filter **/
.cStreamFilter h3 {
    color: #ccc;}
/** New Content Background Border **/
html[dir="ltr"] .ipsStream_withTimeline .ipsStreamItem {
    background: #1a1a1a;}
/** Quote Background **/
.ipsQuote_citation {
    background: #202020;
	color: #ccc}
/** Quote Username **/
.ipsQuote .ipsQuote_citation a {
    color: #ccc;}
.ipsQuote {
    background: #202020;}
.ipsAreaBackground_light {
    background: #1a1a1a;}
.ipsRichEmbed_originalItem.ipsPad, .ipsRichEmbed_originalItem .ipsPad {
    background: #1a1a1a;}
.ipsList_inline, .ipsList_reset {
    background: #1a1a1a;}
/** Edit Box **/
.ksPosts_editedDiv {
    background-color: #000000;}
.ksPosts_editedDiv span {
    color: #ccc !important;}
/** Page Border **/
#ipsLayout_body {
    background: #1a1a1a;}
/** New Content Tab **/
nav div.ipsNavBar_primary ul#elSearchFilter_menu li a, nav div.ipsNavBar_primary ul.ipsNavBar_secondary li a, ul.ipsMenu.ipsMenu_auto.ipsMenu_bottomCenter li.ipsMenu_item a {
    color: #ccc;}
.ipsNavBar_secondary {
    background: #1a1a1a;}
/** Home Activity New Content **/
.ipsBreadcrumb > ul > li {
    color: #ccc;}
nav[data-controller="core.front.core.navBar"] div.ipsNavBar_primary.ipsLayout_container.ipsClearfix {
    background-color: #1a1a1a;}
/** Popular Contributor **/
.ipsTabs_activeItem {
    background: #1a1a1a;
    color: #ccc;}
.ipsPad_half, .ipsApp ul.ipsPad_half, .ipsApp ol.ipsPad_half {
    background: #1a1a1a;}
/** New Content Bottom **/
.ipsBreadcrumb [data-action="defaultStream"], .ipsBreadcrumb [data-action="markSiteRead"] {
    color: #ccc;}
/** Follow Buttom **/
html[dir="ltr"] .ipsFollow > a:not( .ipsCommentCount ), html[dir="ltr"] .ipsFollow > span:not( .ipsCommentCount ), html[dir="ltr"] .ipsFollow > a > span:not( .ipsCommentCount ) {
    background: #282828;}
div.ipsPageHeader div.ipsFollow.ipsButton.ipsButton_link.ipsButton_verySmall {
    background-color: #282828;
    color: #333;}
/** Reply Box **/
.ipsAreaBackground {
    background: #282828;}
.cke_wysiwyg_frame, .cke_wysiwyg_div {
    background-color: #1a1a1a;}
/** Menu Selected Tab **/
.ipsNavBar_primary > ul > li.ipsNavBar_active > a {
    background: #425244;
	color: #ccc;}
/** Dropdown Menu My Streams **/
.ipsMenu_title {
    background: #1a1a1a;}
.ipsMenu {
    background: #000000;}
/** Poll Font **/
.ipsType_sectionHead {
    color: #ccc;}
/** Go To Unread Post **/
.ipsItemStatus:not( .ipsItemStatus_large ) {
    color: #38ab37;}
/** Some Ads Background **/
div#amzn-native-ad-0 div.amzn-native-products-list div.amzn-native-product a.amzn-native-product-title span.amzn-native-product-link-catcher {
    background: #1a1a1a;}
/**  **/
/**  **/
/**  **/
/**  **/
/**  **/
/**  **/
/**  **/
/**  **/
/**  **/
/**  **/
/** Old Stuff Below **/
/** ----- **/
/** ----- **/
/** ----- **/
/** Change border background **/
#content-wrapper { 
background: #121212;
padding: 15px;}

/** Forum navigation background **/
.fj-control-bar .forum-nav-control { 
background-color: #000000;
border: 1px solid #323232;
border-radius: 3px;
padding: 8px;}

/** Post background and text color **/
.left-post-container .post-content-area { 
color: #CCCCCC;
font-family: "arial", "helvetica", sans-serif;}

/** More post background **/
.left-post-container .post-container-inner { 
background: #000000;
overflow: hidden !important;}

/** Bottom of post borders **/
.forum-post-tools { 
border-bottom: 1px none #323232;
border-top: 1px solid #323232;
font-size: 11px;
margin-top: 10px;
padding-bottom: 2px;}

/** Borders around post **/
.left-post-container { 
background: none;}

/** Post header **/
.post-header { 
background-color: #1A1A1A;
color: #CCCCCC;
font-size: 11px;
line-height: 11px;}

/** Search bar**/
#search-area #search { 
background: #000000;
border: 0px none currentColor;
color: #FF0000;
margin: 6px 0px 0px 5px;
width: 88%;}

/** More search bar**/
#search-bar { 
background-color: #000000;
border: 2px solid #BCB9B0;
height: 26px;
margin-left: 0px;
margin-right: 360px;}

/** Logged in box **/
#search-area #loggedin-box { 
background: #000000 !important;
border: 2px solid #323232 !important;
color: #CCCCCC;
padding: 0px 3px 7px !important;}

/** Post **/
.single-post { 
border: 1px solid #323232;
margin-bottom: 10px;
overflow: hidden;
padding: 3px;}

/** Quote box **/
.wiki_markup .quote-container .quote-block { 
background-color: #1a1a1a;
border: 1px solid #323232;
overflow: hidden;
padding: 10px;}

/** Signature **/
.signature { 
border-top: 2px solid #323232;
margin-top: 15px;
padding-top: 10px;}

/** Post buttons **/
.depressed, .btn:hover, .btn:focus, .btn_control a:hover, .btn_control a:focus { 
background-color: #202020;
outline: 0px none currentColor;}

/** Button text color **/
.btn { 
border: 1px solid #AAAAAA;
border-radius: 3px;
color: #CCCCCC !important;
display: block;
float: left;
font-size: 11px;
font-weight: 400;
height: 16px;
line-height: 17px;
padding: 2px 8px;}

/** Button background **/
.forum-control .btn { 
background-color: #202020;
color: #CCCCCC;
margin-bottom: 3px;}

/** Post buttons not-depressed background **/
.btn-grp-right .btn, .btn-grp-right .ilink, .btn-grp-right .iilink, .btn-grp-right .itext, .actionbar .btn-grp-right .component, .btn-grp-right .component, .btn-grp-right div {
background-color: #00FF08;
float: right;
margin-left: 5px;
margin-right: 0px;}

/** Flag button **/
.btn-grp .component, .btn-grp .btn { 
background-color: #202020;
margin-right: 5px;
margin-top: 5px;}

/** Move button icon **/
.icon-post-quote { 
background-position: -12px -7050px;}

/** Post # box **/
.forum-post-num { 
background-color: #202020;
color: #333333;
float: right;
font-weight: 400;
padding: 6px 10px;}

/** Text editor **/
.component.editor textarea, .component.editor .cke_wysiwyg_div, [data-component~="editor"] textarea, [data-component~="editor"] .cke_wysiwyg_div { 
background: #282828;
color: #CCCCCC;}

/** More text editor **/
#quick-reply-editor { 
background-color: #1A1A1A;
border-top: 1px solid #323232;
margin: 25px 0px 0px;
overflow: hidden;
padding: 12px 20px;}

/** Editor buttons **/
.cke_top { 
background: -o-linear-gradient(top, #202020, #202020) #202020;
border-bottom: 1px solid #323232;
box-shadow: 0px 1px 0px #FFFFFF inset;
padding: 6px 8px 2px;
white-space: normal;}

/** Editor bottom **/
.cke_bottom { 
background: -o-linear-gradient(top, #202020, #202020) #202020;
border-top: 1px solid #323232;
box-shadow: 0px 1px 0px #FFFFFF inset;
padding: 6px 8px 2px;
position: relative;}

/** Footer **/
#footer-stats, #footer-content { 
  background-color: #1A1A1A;}

/** Get notified curtain **/
div.select select { 
background: #282828;
  padding: 3px 0px;}

/** Forum navigation curtain **/
.fj-menu-area select { 
background: #282828;
color: #CCCCCC;
  max-width: 300px;}
  
/** Random text **/
body, div, dl, dt, dd, ul, ol, li, h1, h2, h3, h4, h5, h6, pre, code, form, fieldset, legend, input, button, textarea, p, blockquote { 
color: #CCCCCC;
margin: 0px;
  padding: 0px;}

/** New Posts has replied **/
div.viewer-has-replied, tr.viewer-has-replied td { 
background-color: #121212 !important;
}

/** New Posts unread backgrounds **/
table.forum-list-tbl tr td:last-child { 
background: #202020;
border-right: medium none transparent;
}
table.forum-list-tbl td.narrow-col { 
background: #202020;
width: 6%;
}
.last-post-thread-col { 
background: #202020;
width: 30%;
}
table.forum-list-tbl td.thread-col { 
background: #202020;
width: 55%;
}
table.forum-list-tbl td.icon-col { 
background: #202020;
width: 3%;
}

/** New Posts top row **/
table.forum-list-tbl th { 
background-color: #000000;
border-bottom: 1px solid #DDDDDD !important;
border-top: 1px solid #DDDDDD !important;
color: #555555;
font-size: 11px;
font-weight: 700;
padding: 2px 2px 2px 4px !important;
vertical-align: middle;
}

/** New Posts hover thread **/
table.navigation.nostripe tr:hover td { 
background-color: #202020;
}
tr.viewer-has-replied:hover td { 
background-color: #121212 !important;
}

/** New Posts text **/
a, a:link, a:visited { 
color: #CCCCCC;
text-decoration: none;
}}
