css:
@-moz-document domain("github.com") {
pre,code,tt,.gist .gist-file,.diff-line-code {
  font-family:'Source Code Pro for Powerline','Envy Code R for Powerline',Consolas,"PT Mono","Andale Mono",Monaco,"Liberation Mono","Courier New",monospace !important;
  font-size:12px !important;
}
    
#files .file .data .line_numbers,.file-box .data .line_numbers {
  padding:0 6px !important;
  line-height:21px !important
}
.highlight,.highlight pre,.diff-line-num,.diff-line-nums,.file-code-line,
.hide-line-numbers .diff-line-num:before,.diff-line-num a,.diff-line-num span,.blob-line-nums a,.blob-line-nums span {
  background:#23241f !important;
  font-size:12px !important;
  line-height:21px !important
}
.highlight,.highlight .err,.highlight .n,.highlight .nb,.highlight .ni,.highlight .nl,.highlight .nn,.highlight .nx,
.highlight .py,.highlight .nv,.highlight .bp,.highlight .vc,.highlight .vg,.highlight .vi,.highlight .p,.highlight .w,
.diff-line-num a,.diff-line-num span,.blob-line-nums a,.blob-line-nums span,.diff-line-num,.diff-line-nums { color:#f8f8f2 !important }
.highlight span[class] {
  font-weight:normal !important;
  font-style:normal !important
}
.diff-line-num, .blob-line-nums { border-right: 1px solid #75715e !important }
.highlight pre { border-radius:0 !important }
.highlight .c,.highlight .cm,.highlight .cp,.highlight .c1,.highlight .cs,.highlight .diff-line-code { color:#75715e !important }
.highlight .err { background:#f82167 !important }
.highlight .k,.highlight .kn,.highlight .nt,.highlight .o,.highlight .ow,
.highlight .gd .diff-line-num span,.highlight .gd .diff-line-code { color:#f92672 !important }
.highlight .kc,.highlight .kd,.highlight .kp,.highlight .kr,.highlight .kt,.highlight .no { color:#66d9ef !important }
.highlight .na,.highlight .nc,.highlight .nd,.highlight .ne,.highlight .nf,
.highlight .gi .diff-line-num span,.highlight .gi .diff-line-code { color:#a6e22e !important }
.highlight .ge { font-style:italic !important }
.highlight .gs { font-weight:bold !important }
.highlight .l,.highlight .m,.highlight .mf,.highlight .mo,.highlight .mh,.highlight .mi,.highlight .il,.highlight .se { color:#ae81ff !important }
.highlight .ld,.highlight .s,.highlight .sb,.highlight .sc,.highlight .sd,.highlight .s1,.highlight .s2,
.highlight .sh,.highlight .si,.highlight .sx,.highlight .sr,.highlight .ss { color:#e6db74 !important }
.highlight .diff-line-code,.highlight .gd,.highlight .gi { background:#23241f !important }
.highlight .gd { color:#f92672 !important }
.highlight .gi { color:#a6e22e !important }
.highlight .gd .x { background:#f92672 !important }
.highlight .gi .x { background:#a6e22e !important }
.highlight .gu { background:#75715e !important }
.highlight .gu,.highlight .gd .x,.highlight .gi .x {
  padding:1px !important;
  color:#23241f !important
}
.highlight .w,.highlight .gc { color:#f8f8f2 !important }
.highlight .gc,.highlight .gc .line { background:#75715e !important }
.highlight .file-diff-line:hover .diff-line-num,.highlight .file-diff-line:hover .diff-line-num span,
.highlight .file-diff-line:hover .diff-line-code,[style="background-color: rgb(255, 255, 204);"] { background:#3b392f !important }
.file-diff-line,.file-code-lines { text-shadow:none !important }
.highlight .octicon { color:inherit !important }
.diff-line-num,.diff-line-nums,.file-code-line,
.hide-line-numbers .diff-line-num:before,.diff-line-num a,.diff-line-num span,.blob-line-nums a,.blob-line-nums span {
  font-size: 12px !important;
  color: #75715e !important
}
}

@-moz-document domain("gist.github.com") {
#files .file-data .line-numbers,.file-box .file-data .line-numbers,.revision .file-box .data .line-number {
  padding:8px 5px !important;
  line-height:21px !important
}
#files .file-data .line-data,.file-box .file-data .line-data,.revision .file-box .data .line-number {
  padding-top:6px !important;
  padding-bottom:6px !important
}
#files .file-data .line .gu,.file-box .file-data .line .gu,#files .file-data .line .gu pre,.file-box .file-data .line .gu pre {
  background:#75715e !important;
  color:#f8f8f2 !important
}
#files .file-data .line div[class=""],.file-box .file-data .line div[class=""] {
  color:#75715e !important
}
.revision .file-box .file-data .line-number {
  padding-top:0 !important;
  padding-bottom:0 !important
}
}
