css:
@-moz-document domain("telebears.berkeley.edu") {
body, table, div, tr, td
{
  	font-family: Helvetica;
  	font-weight: 100;
  	line-height: 1.75em;
}

#toptabs th, #toptabs td, #toptabs .logout
{
  	background-color: transparent !important;
  	border: none !important;
  	font-size: 14px !important;
}

.tabs th a:link, .tabs th a:visited, .tabs .protected
{
  	color: white;
}

.form-layout .input, input[type="text"]
{	
  	background-color: white;
}

#bodycol td 
{
  	padding-top: 0px !important;
  	padding-bottom: 0px !important;
    padding-right: 0px !important;
    padding-left: 0px !important;
}

#bodycol table
{
  	width: 100%;
}

#bodycol table th, .class-list-content th
{
  	background-color: #efefef;
  	color: black;
  	font-weight: 100;
}

#bodycol table td, .class-list-content td
{
  	background-color: #efefef;
}

html
{
  	width: 100%;
}

b, strong
{
  	font-weight: 500;
}

body
{
  	padding-top: 0px;
  	background: #eee;
  	padding-bottom: 100px;	
  	width: 100%;
  	min-width: 1000px;
}

div.form-layout
{
  	max-width: 100%;
}

#toptabs table
{
  	width: 100%;
  	height: 40px;
  	background-color: rgba(158, 202, 190, 1);
}

#bodycol caption
{
  	background: transparent;
}

#toptabs 
{
  	margin-top: 0px;
  	padding-top: 0px;
  	margin: 0 0 0 0;
  	padding: 0 0 0 0;
}

#topmodule
{
  	display: none;
}

.menuheader
{
  	font-style: normal;
}

#main
{
  	padding-left: 100px;
  	padding-right: 100px;
  	margin-top: 50px;
  	opacity: 0.9
}

#main tr
{
  	background-color: white;
}

#main td
{
  	padding-top: 50px;
  	padding-bottom: 50px;
  	padding-left: 25px;
  	padding-right: 25px;
}
#banner
{
  	display: none;
}

.round-tl, .round-tr
{
  	background-image: none;
}

.tasknav
{
  	background-color: white;
  	border-bottom: 0px;
}

table, td, tr
{
  	background-color: none;
}

#topmodule
{
  	border-bottom: 0px;
}

#toptabs
{
  	border-bottom: 0px;
  	margin-bottom: 10px;
}

.menubody, #projecttools .menubody
{
  	background-color: white;
  	border: none !important;
  	opacity: 0.9;
}

#projecttools
{
  	background: none;
  	background-color: none;
  	border: none;
}

.rule, .invisible, .copyright
{
  	display: none;
}

hr
{
  	display: none;
}

}
