@-moz-document domain("userstyles.org") {
/* STYLISH MIN - DAYLO  */
/* ~ LICENSE - CC-BY ~  */
/* VER 1.8
	Social fix + ads    */
@font-face {
  font-family: 'Open Sans Light';
  font-style: normal;
  font-weight: 300;
  src: local('Open Sans Light'), local('OpenSans-Light'), url(https://themes.googleusercontent.com/static/fonts/opensans/v8/DXI1ORHCpsQm3Vp6mXoaTXhCUOGz7vYGh680lGh-uXM.woff) format('woff');
}
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans'), local('OpenSans'), url(https://themes.googleusercontent.com/static/fonts/opensans/v8/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}
@font-face {
  font-family: 'Open Sans Semibold';
  font-style: normal;
  font-weight: 600;
  src: local('Open Sans Semibold'), local('OpenSans-Semibold'), url(https://themes.googleusercontent.com/static/fonts/opensans/v8/MTP_ySUJH_bn48VBG8sNSnhCUOGz7vYGh680lGh-uXM.woff) format('woff');
}
@font-face {
  font-family: 'Open Sans Bold';
  font-style: normal;
  font-weight: 700;
  src: local('Open Sans Bold'), local('OpenSans-Bold'), url(https://themes.googleusercontent.com/static/fonts/opensans/v8/k3k702ZOKiLJc3WVjuplzHhCUOGz7vYGh680lGh-uXM.woff) format('woff');
}
#main-header {
    background-color: transparent!important;
    font-family: "Open Sans Light", Open Sans Light, Open Sans, sans-serif !important;
    font-size: 1.5em!important;
    text-align: center;
    text-decoration: none!important;
}

#main-header a {
    color: rgb(0,0,255)!important;
    text-decoration: none!important;
}

#main-header a:hover {
    color: rgb(120,120,255)!important;
}

#content-wrapper {
  background-color: #f7f7f7!important;
  border-radius: 0.4em;
}

#left-sidebar a,#show-advanced-search,footer a {
    color: rgb(100,100,100)!important;
    text-decoration: none!important;
}

#left-sidebar a:hover,#show-advanced-search:hover,footer a:hover {
    color: rgb(100,100,255)!important;
}

a {
    text-decoration: none!important;
}

body {
    font-family: "Open Sans", Open Sans, sans-serif !important;
}

footer {
    text-align: center!important;
}

#left-sidebar > * {
    margin-bottom: 10px;
    border: 1px solid rgb(221, 221, 221);
    padding: 5px;
    border-radius: 5px;
    background-color: transparent!important;
}

h3,h2, #more-screenshots span, th {
    font-family: "Open Sans Light", Open Sans Light, Open Sans, sans-serif !important;
    text-decoration: none!important;
    font-weight: 600;
}

th {
  font-family: "Open Sans Semibold", Open Sans Semibold, Open Sans, sans-serif !important;
    font-size: 1em!important;
  font-weight: normal;
}

.install {
    background-image: linear-gradient(rgb(60, 150, 60), rgb(60, 150, 74))!important;
}

.install:hover {
    background-image: linear-gradient(rgb(100, 150, 100), rgb(100, 160, 114))!important;
}

a:link {
    color: rgb(0,0,255)!important;
    text-decoration: underline;
}

a:active {
    color: #0000ff!important;
    text-decoration: underline;
}

a:visited {
    color: rgb(80,80,80)!important;
}

a:visited:hover {
    color: rgb(120,120,120)!important;
}

a:hover {
    color: rgb(100,100,255)!important;
    text-decoration: none;
}
textarea {
  width: 90%;
}
p {
  width: 90%;
}
div[id^="stylish-installed-style-installed"],div[id*="stylish-installed-style-installed"] {
  height: 30px;
}
a.install-note {
  color: #fff !important;
  text-shadow: 0px 1px 1px rgba(0, 0, 0, 1);
}
img[src^="https://df6a.https.cdn.softlayer.net/80DF6A/static.userstyles.org/style_screenshots/"],img[src*="https://df6a.https.cdn.softlayer.net/80DF6A/static.userstyles.org/style_screenshots/"] {
  width: 70%;
  text-align: left;
}
#social {
  margin-bottom: 0 !important;
  margin-top: 0.5em !important;
}
div[id^="___plusone"], iframe[src^="https://apis.google.com/u/"] {
  width: 70px !important;
  height: 20px !important;
}
div.ad {
  display: none;
}
}
