css:
@-moz-document domain(forums.somethingawful.com)
{
	img.timg
	{
		visibility:visible !important;
		border:2px #0077AA solid !important;
		max-height:200px;
		max-width:170px;
	}
	img.timg_big
	{
		border:2px #0077AA solid !important;
	}
	span.timg_wrapper
	{
		position:relative;
		white-space:nowrap;
	}
	span.timg_sizer
	{
		visibility:hidden;
		position:absolute;

		background-color: #0077AA;
		border:2px #0077AA solid;
		color:white !important;

		text-align:center;
		cursor:pointer;

		z-index:10;
		left:0;
		width:1em;
		height:1em;
	}
}
