@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com")
{

body,
._2dpb,
._3zm-:hover a,
._3zm- a:focus,
._3zm- a {
    text-shadow: none !important;
}


/* News feed */
/* "Post" screen transitions */
.uiTypeahead .uiTextareaAutogrow,
#composerCityTagger,
._1dsr,
._9lb,
#pagelet_composer .uiSelectorButton {
    transition: 0.2s !important;
}

/* In the news feed, there's the "who can see" button, this blocks the transition used above, avoiding graphic glitches */
#pagelet_composer .uiToggle .uiSelectorButton {
    transition: 0s !important;
}

/* "Post" screen buttons effect on hover */
#composerCityTagger:hover,
._1dsr:hover {
    box-shadow: inset 0 0 1px #000;
    margin-top: -2px !important;
    height: 45px !important;
}

/* Right chatbar */
.tickerStoryClickable ._54ru {
    border-radius: 100px;
    transition: 0.3s;
}
.tickerStoryClickable:hover ._54ru {
    border-radius: 100px;
}
.tickerStoryClickable {
    transition: 0.2s;
}
.tickerStoryClickable:hover {
    background: rgba(0, 0, 0, 0.04);
}

/* This adds the extra spacing I need to add "borders" on each chat div */
.uiScrollableAreaContent ._55ln,
#sidebar_navigation_top ._4g5p,
._4gb_ {
    margin-top: 1px;
    padding-top: 2px !important;
    padding-bottom: 2px !important;
    transition: 0.2s;
}

/* There's a background in the number counter, this applies transitions to it */
._4vsz {
    transition: 0.2s;
}

/* Feed page round images */
._5pb8 .img,
.ego_unit ._s0 {
    transition: 0.3s;
}

/* Removes a border around the profile picture in the news feed */
._5v9u:after {
    border: none !important;
}

/* Effects when hovering the right action bar */
.ego_unit {
    width: 260px !important;
    padding: 5px !important;
    margin-left: -8px !important;
}
.ego_unit:hover {
    background: rgba(0, 0, 0, 0.1);
}

/* Chatbar round profile pictures */
._55lt .img,
._521h ._4-k7 .img {
    transition: 0.3s;
}

/* Removes the border around the profile picture on the chatbar */
._5pr2 ._56p9:after {
    border: none !important;
}

/* Removes adds on the already small chatbar. */
#pagelet_canvas_nav_content,
#apps_gripper {
    display: none !important;
}

/* post commnet */
.UFIRow.UFIFirstComponent,
.timelineUnitContainer,
.fbTimelineFeedbackActions {
    border-top: none !important;
}

/* post message */
.uiTypeahead {
    border: none !important;
}
.uiTypeahead .wrap {
    border: none !important;
}

/* Chat Side Bar */
.fbChatSidebar {
    border-left: none !important;
}
.fbSidebarGripper {
    background-color: #fff;
}
.fbChatSidebarBody {
    background-color: #fff;
}
._51mx {
    background-color: #fff;
}

/* Chat Room Size & Style */
.fbNubFlyout {
    height: 420px !important;
}
._50mz.opened {
    width: 350px !important;
}
._5q5b .fbNubFlyoutTitlebar {
    background: #4172b0 !important;
    border: 1px solid #4172b0 !important;
}

/* Chat Room Profile */
.fbChatConvItem .profileLink:after {
    background-image: none !important;
}
.fbChatMessageGroup .profileLink {
    box-shadow: none !important;
}
.fbChatConvItem .profileLink:before {
    background: transparent;
    display: block;
    width: 32px;
    height: 32px;
    content: "";
    position: absolute;
    box-shadow: none;
}

/* Chat Text */
.titlebar .titlebarText {
    text-shadow: none !important;
    font-weight: normal;
}

/* iOS7 Style */
._1nc6 ._5w1r {
    color: black;
    font-size: 13px !important;
    background-color: #ddeeff;
    font-family: "Helvetica Neue", Arial;
    text-shadow: none !important;
    background-image: none;
    border: none;
    -webkit-box-shadow: none;
    margin: 0px 0px 0px 0px;
    min-height: 14px;
    padding: 6px 9px 5px 8px;
    position: relative;
    font-weight: 100;
}
._1nc6 ._5w1r:after,
._1nc6 ._5w1r:before,
._5w1r:after,
._5w1r:before {
    visibility: hidden;
}
._kso a {
    color: black;
}
._50kd ._kso,
._50kd ._kso:before {
    background-color: #f0f0f5;
    background-image: none;
    color: black;
    font-family: "Helvetica Neue", Arial;
    font-size: 13px;
}
._50kd ._kso,
._50kd ._kso a {
    color: black;
}
._1nc7 ._5w1r {
    background-color: #f0f0f5;
    border: none;
    -webkit-box-shadow: none;
    margin: 2px 0px 7px 5px;
    min-height: 14px;
    padding: 6px 9px 5px 8px;
    position: relative;
    text-shadow: none;
    background-image: none !important;
}
._50kd ._kso {
    padding: 6px 9px 5px 8px;
    margin-top: 9px;
}
._5w-6 {
    font-weight: normal;
}
._50dw .profileLink:after {
    background-image: none;
}
._50kd ._kso:before {
    left: auto;
    display: none;
    right: 100%;
}
._kso:before {
    background-image: none;
}
._kso:before,
._kso:after {
    background-image: none;
    background-repeat: no-repeat;
    background-size: auto;
    background-position: -218px -433px;
    height: 13px;
    top: 5px;
}
._kso:before {
    background-image: none;
}
.fbDockChatTabFlyout .fbNubFlyoutBody {
    background-color: white;
}
.fbChatConvItem:hover .metaInfoContainer {
    visibility: hidden;
}
._5w-6 {
    background-color: white !important;
}
._50kd ._kso:after,
._5w-6 {
    background-image: none;
}
._kso:before,
._kso:after {
    background-color: white
}
.conversation {
    background-image: none;
    color: black;
    font-family: "Helvetica Neue", Arial;
    font-size: 13px;
}
._1nc7 ._5ys_ {
    left: 2px;
}
._5ys_ img {
    width: 32px;
    height: 32px;
}
._5ys_:after,
._1nc7 ._5w1r:after {
    background-image: none !important;
}
._38vo:after {
    border: none !important;
}
._4tdt ._31o4 {
    padding-bottom: 9px;
}
#u_0_0 {
    display: none;
}

}
