css:
@-moz-document domain('wikipedia.org'){
    @font-face {
        font-family: 'Source Code Pro';
        font-style: normal;
        font-weight: 400;
        src: local('Source Code Pro'), local('SourceCodePro-Regular'), url(https://themes.googleusercontent.com/static/fonts/sourcecodepro/v4/mrl8jkM18OlOQN8JLgasDxBHWFfxJXS04xYOz0jw624.woff) format('woff');
    }

    body {
        background: #fff !important;
        font-size: 16px !important;
    }
    
    body, input, div, p, span {
        font-family: 'Arial' !important;
    }
    
    b {
        font-weight: bold !important;
    }
    
    #content > h1 {
        border: 0px !important;
        font-family: 'Arial' !important;
        font-weight: 700 !important;
        font-size: 3em !important;
    }
    
    h2 {
        border: 0px !important;
        font-family: 'Arial' !important;
        font-weight: 400 !important;
        font-size: 26px !important;
    }
    
    h3 {
        border: 0px !important;
        font-family: 'Arial' !important;
        font-weight: 400 !important;
        font-size: 16px !important;
    }
    
    table {
        box-shadow: none !important;
    }
    
    #content h3 {
        font-size: 20px !important;
    }
    
    p {
        font-weight: 300 !important;
        line-height: 1.9 !important;
    }
    
    table, td, th {
        border: 0px !important;
        border-collapse: collapse !important;
        padding: 5px !important;
    }
    
    ul, li {
        background: transparent !important;
        border: 0px !important;
        line-height: 1.8 !important;
        font-size: 14px !important;
        font-weight: 300 !important;
    }
    
    li span {
        background: transparent !important;
        border: 0px !important;
        font-size: 14px !important;
    }
    
    .reference, hr {
        display: none;
    }
    
    dl {
        font-weight: 300 !important;
    }
    
    dt {
        font-style: italic;
        font-size: 16px !important;
        margin-top: 10px !important;
    }
    
    dd {
        font-size: 15px !important;
    }
    
    #p-logo a {
        background-image: url('http://icons.iconarchive.com/icons/sykonist/popular-sites/128/Wikipedia-icon.png') !important;
    }
    
    code {
        padding: 3px !important;
        background: transparent !important;
        color: #39892f !important;
    }
    
    tt {
        font-family: 'Source Code Pro', 'Lucida Sans Unicode', 'Courier New' !important;
        font-size: 16px !important;
    }
    
    a, a:link, a:hover, a:visited {
        color: #0645ad !important;
    }

    .new, .new:link, .new:visited {
        color: #cd5b45 !important;
    }
    
    blockquote {
        border: 0px !important;
        font-style: italic !important;
        font-size: 20px !important;
        margin: 35px 75px !important;
        line-height: 1.5 !important;
        font-weight: 300 !important;
        background: transparent !important;
    }
    
    select {
        padding: 0px 10px;
    }
    
    .mw-ui-button, .mw-ui-progressive {
        background: #347bff !important;
        color: #fff !important;
        text-shadow: none !important;
        box-shadow: none !important;
        border: none !important;
        font-size: 1em !important;
        padding: 9px 16px !important;
        margin: 5px 10px 5px 0px !important;
        font-size: 0.875em !important;
        
    }
    
    .mw-ui-button, .mw-ui-progressive, .mw-ui-button:link, .mw-ui-progressive:link, .mw-ui-button:visited, .mw-ui-progressive:visited {
        color: #fff !important;
    }
    
    .mw-ui-button:hover,  mw-ui-progressive:hover {
        background: rgba(52, 123, 255, 0.85) !important;
    }
    
    .mw-ui-button:active, mw-ui-progressive:active {
        box-shadow: 0 0 15px 0 rgba(0, 0, 0, 0.3) inset !important;
    }
    
    #wpSave, #wpPreview, #wpDiff {
        border-radius: 4px !important;
        border: 1px solid #347Bff !important;
        color: #347Bff !important;
        background: #fff !important;
        text-shadow: 0px 0px 0px;
        padding: 0.5em 1em;
        cursor: pointer;
    }
    
    #wpSave:hover, #wpPreview:hover, #wpDiff:hover {
        color: rgba(52, 123, 255, 0.5) !important;;
    }
    
    
    
    /* Search */
    #simpleSearch {
        border: 0 !important;
        margin: 0 !important;
        background: #fff !important;
    }
    
    #searchInput, .mw-ui-input, .oo-ui-inputWidget-input {
        border-bottom: 2px solid #347bff !important;
        border-top: none !important;
        border-left: none !important;
        border-right: none !important;
        background: transparent !important;
        color: #000 !important;
        padding: 7px !important;
        font-size: 1em !important;
    }
    
    .mw-ui-input:focus {
        box-shadow: none !important;
    }
    
     .oo-ui-inputWidget-input:focus {
        border-bottom: 2px solid #347bff !important;
        border-top: none !important;
        border-left: none !important;
        border-right: none !important;
     }
	
    #search {
        border: 0px !important;
        background: transparent !important;
        font-size: 16px !important;
        padding: 4px 7px 7px 7px !important;
    }
	
    #searchButton {
        right: 7px !important;
        top: 7px !important;
    }
	  
    
    
    /* Search results in box */
    .suggestions {
        font-size: 0.875em !important;
        max-width: 300px !important;
        margin-top: 15px !important;
        border: 0px !important;
        box-shadow: 0 10px 10px 0 rgba(0, 0, 0, 0.3);
    }
	
    .suggestions-results {
        border: 0px !important;
    }
    
    .suggestions-result-current {
        background: #e5e5e5 !important;
        color: #000 !important;
    }
	
    .suggestions-result, .suggestions-special {
        padding: 10px !important;
    }
    
    .suggestions-special {
        border: none !important;
    }
    
    .suggestions-special div {
        color: #000 !important;
        font-style: italic !important;
    }
	
    .imeselector, #searchEngines {
        display: none !important;
    }
	
    .mw-search-formheader {
        border: 0px !important;
        background: transparent !important;
    }
	
    .mw-search-result-heading, .searchresult, .searchmatch {
        font-size: 14px !important;
        font-weight: 400 !important;
        padding: 3px 0px !important;
    }
	
    .searchmatch {
        font-weight: 700 !important;
    }
	   
    
    
    /* Search results on page */
    .mw-search-result-data {
        color: #808080 !important;
        font-size: 0.875em !important;
    }
    
    
    
    
    /* Page */
    #mw-page-base, .vectorTabs {
        background: transparent !important;
        border: 0px !important;
    }
    
    #content {
        border: 0px !important;
        margin-left: 215px !important;
    }
    
    .portal {
         background: transparent !important; 
    }
    
    #mw-panel {
        width: 170px !important;
        box-shadow: 5px 0px 0px 0 rgba(0, 0, 0, 0.1);
    }
    
    #mw-panel ul > li > a{
        color: rgba(0, 0, 0, 0.6) !important;
    }
    
    #mw-panel ul > li > a:hover {
        color: #0645ad !important;
    }
    
    #mw-head {
        opacity: 1.0;
        -webkit-transition-property: opacity !important;
        -moz-transition-property: opacity !important;
        -o-transition-property: opacity !important;
        -ms-transition-property: opacity !important;
        transition-property: opacity !important;
        -webkit-transition-duration: 0.5s !important;
        -moz-transition-duration: 0.5s !important;
        -o-transition-duration: 0.5s !important;
        -ms-transition-duration: 0.5s !important;
        transition-duration: 0.25s !important;
    }
    
    #mw-head:hover {
        opacity: 1.0 !important;
    }
    
    .uls-settings-trigger {
        display: none !important;
    }
    
    #footer {
        padding-left: 3em !important;
    }
    
    
    
    /* Заглавная, обсуждение */
    #left-navigation {
        margin-left: 233px !important;
    }
    
    
    
    /* Standard table */
    .wikitable {
        font-weight: 300 !important;
    }
	
    .wikitable td, .wikitable th {
        border: 1px solid rgba(0, 0, 0, 0.07) !important;
    }

    
    
    /* Code */
    pre {
        line-height: 1.3 !important;
        font-family: 'Source Code Pro', 'Lucida Sans Unicode', 'Couriew New', 'Arial' !important;
        border: 0px !important;
        background: 0px !important;
        font-size: 16px !important;
    }
	
    .mw-code {
        border: 0px !important;
        padding: 17px !important;
        background: 0px !important;
        font-size: 16px !important;
    }
    
    
    
    /* Index */
    .toc {
        background: #fff !important; 
        display: block !important;
        max-width: 300px !important;
        border-left: 0px solid #347bff !important;
        border-top: none !important;
        border-right: none !important;
        border-bottom: none !important;
        opacity: 0.9 !important;
        font-size: 0.875em !important;
    }
    
    .toc div, .toc span {
        font-size: 0.875em !important;
    }
    
    .toc, #toc, .toccolours {
        padding: 0 !important;
        margin-top: 25px !important;
    }
    
    #toc #toctitle, .toc #toctitle, #toc .toctitle, .toc .toctitle {
        text-align: left !important;
    }
	
    .toctoggle {
        display: none !important; 
    }
    
    
    
    /* Block with image */
    .thumbinner, .thumb, .toccolours, .graytable {
        background: transparent !important;
        border: 0px !important;
        font-size: 16px !important;
    }
	
    .thumbimage {
        border: 0px !important;
    }
	
    .thumbcaption {
        font-style: italic !important;
        margin: 7px 0px !important;
    }

    
    
    /* Курсивный небольшой жирноватый шрифт в начале страницы */
    .dablink {
        margin: 14px 0px !important;
        padding: 0 !important;
        font-weight: 300 !important;
        font-size: 18px !important;
        color: #4c4c4c !important;
    }
    
    #contentSub {
        margin: 15px 0 !important;
    }
    
    .rellink {
        padding: 0 !important;
    }
    
    .hatnote {
        padding: 0 !important;
    }
    
    
    
    /* Надпись - материал из вики и текущая страница не проверялась */
    #siteSub, .flaggedrevs_preview {
        display: none !important;
    }
    
    
    
    /* Tables under a page */
    .ambox, .tmbox, .Note, .informationbox {
        margin: 5px 0px !important;
        font-size: 18px !important;
        font-weight: 700 !important;
        line-height: 1.5 !important;
        border: 1px dashed #aaa !important;
        max-width: 550px !important;
        font-weight: 300 !important;
    }
	
    .ambox td, .tmbox td {
        padding: 10px !important;
    }
	
    .ambox-text-small, .mbox-text, .Note td {
        font-size: 14px !important;
        font-weight: 300 !important;
        color: #000 !important;
    }
	
    .ambox-image, .mbox-image {
        display: none;
    }
	
    #request_for_deletion, .ambox-serious {
        background: rgba(255, 0, 0, 0.1) !important;
        border: 1px dashed rgba(255, 0, 0, 0.15) !important;
        color: #a00000 !important;
    }
	
    .tmbox-notice, .tmbox-move, .Note {
        background: rgba(150, 75, 0, 0.1) !important;
        border: 1px dashed rgba(150, 75, 0, 0.15) !important;
    }
	
    .informationbox {
        background: #fff !important;
        padding: 10px !important;
        font-size: 14px !important;
    }
	
    .editOptions {
        background: #fff !important;
        border: 0px !important;
    }
    
    
    
    /* Ссылки на категории */
    .catlinks {
        background: transparent !important;
        border: 1px dashed #aaa !important;
        font-size: 16px !important;
    }
    
    
    
    /* Tables on the right */
    .infobox, .tright, .toccolours {
        background: transparent !important;
        border-collapse: collapse !important;
        border: 1px solid rgba(0, 0, 0, 0.07) !important;
        box-shadow: none !important;
    }
	
    .infobox td, .infobox th, .tright td, .tright th, .toccolours td, .toccolours th {
        padding: 5px !important;
        background: transparent !important;
        border: 0 !important;
    }
    
    
    
    /* Главный блок на заглавной странице */
    .globegris {
        background: transparent !important;
    }
    
    
    
    /* Мини-символы у ссылки править и примечаний */
    .mw-editsection-bracket, .references-small b, .mw-cite-backlink, .plainlinksneverexpand {
        display: none !important;
    }
	
    #floating_object {
        display: none;
    }
    
    
    
    /* Нижние ссылки по навигации и навигация по разделам навигации*/
    .navbox, .nowraplinks {
        margin: 10px 0px !important;
        border-collapse: collapse !important;
    }
	
    .navbox td, .navbox th, .nowraplinks td, .nowraplinks th { 
        padding: 9px 6px !important;
        font-size: 14px !important;
        font-weight: 300 !important;
        border: 1px solid #fff !important;
    }
	
    .NavFrame {
        border: 0px !important;
        background: transparent !important;
        padding: 0px !important;
    }
	
    .NavHead {
        margin: 10px 0px !important;
        padding: 12px !important;
        font-size: 16px !important;
        font-weight: 700 !important;
        background: transparent !important;
    }
	
    .Boxmerge {
        border: 0px !important;
    }
    
    
    
    /* Coordinates */
    #coordinates {
        display: none !important;
    }
	
    #mw-fr-revisiontag, #mw-fr-reviewnotice {
        display: none !important;
    }
    
    
    
    /* New boxes */
    .oo-ui-buttonElement-framed.oo-ui-widget-enabled.oo-ui-buttonElement-active > .oo-ui-buttonElement-button {
        color: #fff !important;
        background: #347bff !important;
    }
    
    .oo-ui-labelElement-label {
        text-shadow: none !important;
    }
    
    .oo-ui-optionWidget-pressed {
        background: #fff !important;
    }
    
    .oo-ui-optionWidget-selected {
        background: rgba(0, 0, 0, 0.1) !important;
    }
    
    
    /* Tooltip */
   .referencetooltip {
       background: #fff !important;
       box-shadow: 2px 2px 10px 2px rgba(0, 0, 0, 0.5) !important;
   }
    
    
    
    /* Popup */
    #mwe-popups-settings {
        box-shadow: none !important;
    }
}
